import Foundation; struct TitleEntry { let title: String; let categories: Set<Category>; let packs: Set<Pack>; let obscurity: Int; init(_ title: String, categories: Set<Category>, packs: Set<Pack>, obscurity: Int) { self.title = title; self.categories = categories; self.packs = packs; self.obscurity = obscurity } }; let offlineObscure: Pack = .offlineObscure; let offlineCelebrities: Pack = .offlineCelebrities; let offlineMovieNight: Pack = .offlineMovieNight; let obscureCount = allTitles.filter { $0.packs.contains(.offlineObscure) }.count; let celebCount = allTitles.filter { $0.packs.contains(.offlineCelebrities) }.count; let movieCount = allTitles.filter { $0.packs.contains(.offlineMovieNight) }.count; print("Obscure: \(obscureCount), Celebrities: \(celebCount), Movie: \(movieCount)")
